mapper.select:
SELECT 

FROM busi_assetname a

WHERE a.id = #{id}

SELECT 

FROM busi_assetname a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.category_id = #{category.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM busi_assetname a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE busi_assetname SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
