mapper.select:
SELECT 

FROM busi_place a

WHERE a.id = #{id}

SELECT 

FROM busi_place a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.office_id = #{office.id}


		AND a.area_id = #{area.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM busi_place a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE busi_place SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
